import re

def parse_rule(rule):
    keyword, source, destination = rule.split(' ', 2)
    source = re.compile('^' + source.replace('?', '.') + '$')
    destination = re.compile('^' + destination.replace('?', '.') + '$')
    return keyword, source, destination

def is_allowed(packet, rules):
    for keyword, source, destination in rules:
        if source.match(packet[0]) and destination.match(packet[1]):
            return keyword == 'permit'
    return False

def main():
    while True:
        n, m = map(int, input().split())
        if n == 0 and m == 0:
            break
        rules = []
        for _ in range(n):
            rules.append(parse_rule(input()))
        packets = []
        for _ in range(m):
            packets.append(input().split())
        allowed_packets = [packet for packet in packets if is_allowed(packet, rules)]
        print(len(allowed_packets))
        for packet in allowed_packets:
            print(' '.join(packet))

if __name__ == "__main__":
    main()
