#include <stdio.h>
#include <stdlib.h>

#define MAX_SIZE 5

int max(int a, int b) {
    return (a > b) ? a : b;
}

int max_rectangle(int map[MAX_SIZE][MAX_SIZE], int row, int col) {
    int max_size = 0;
    int row_start = 0;
    int row_end = 0;
    int col_start = 0;
    int col_end = 0;

    int *row_sum = (int *)malloc(MAX_SIZE * sizeof(int));
    int *col_sum = (int *)malloc(MAX_SIZE * sizeof(int));

    for (int i = 0; i < MAX_SIZE; i++) {
        row_sum[i] = 0;
        col_sum[i] = 0;
    }

    for (int i = 0; i < MAX_SIZE; i++) {
        for (int j = 0; j < MAX_SIZE; j++) {
            if (map[i][j] == 1) {
                row_sum[i]++;
                col_sum[j]++;
            }
        }
    }

    for (int i = 0; i < MAX_SIZE; i++) {
        for (int j = 0; j < MAX_SIZE; j++) {
            if (map[i][j] == 1) {
                row_start = max(row_start, row_sum[i]);
                col_start = max(col_start, col_sum[j]);
                row_end = max(row_end, row_sum[i] + col_sum[j] - 1);
                col_end = max(col_end, row_sum[i] + col_sum[j] - 1);
                max_size = max(max_size, row_sum[i] + col_sum[j]);
            }
        }
    }

    free(row_sum);
    free(col_sum);

    return max_size;
}

int main() {
    int m;
    scanf("%d", &m);

    for (int i = 0; i < m; i++) {
        int map[MAX_SIZE][MAX_SIZE];
        for (int j = 0; j < MAX_SIZE; j++) {
            for (int k = 0; k < MAX_SIZE; k++) {
                scanf("%d", &map[j][k]);
            }
        }

        int max_size = max_rectangle(map, MAX_SIZE, MAX_SIZE);
        printf("%d\n", max_size);
    }

    return 0;
}
