#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int maxRectangle(vector<vector<char>>& map) {
    int m = map.size();
    int n = map[0].size();
    vector<int> height(n, 0);
    int maxArea = 0;

    for (int i = 0; i < m; i++) {
        int minHeight = 0;
        for (int j = 0; j < n; j++) {
            if (map[i][j] == '1') {
                height[j] = minHeight + 1;
            } else {
                height[j] = 0;
                minHeight = 0;
            }
            minHeight = max(minHeight, height[j]);
        }

        for (int j = 0; j < n; j++) {
            int area = height[j] * (n - j);
            maxArea = max(maxArea, area);
        }
    }

    return maxArea;
}

int main() {
    int m;
    cin >> m;

    for (int i = 0; i < m; i++) {
        vector<vector<char>> map;
        string line;
        getline(cin, line); // ignore empty line

        while (getline(cin, line)) {
            vector<char> row;
            for (char c : line) {
                row.push_back(c);
            }
            map.push_back(row);
        }

        cout << maxRectangle(map) << endl;
    }

    return 0;
}
