import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int m = scanner.nextInt();
        scanner.nextLine(); // Consume newline left-over

        for (int i = 0; i < m; i++) {
            scanner.nextLine(); // Skip empty line
            String[] map = new String[5];
            for (int j = 0; j < 5; j++) {
                map[j] = scanner.nextLine();
            }

            int maxArea = 0;
            for (int row = 0; row < 5; row++) {
                for (int col = 0; col < 5; col++) {
                    if (map[row].charAt(col) == '1') {
                        int area = 1;
                        int rowEnd = row;
                        int colEnd = col;

                        // Expand to the right
                        while (colEnd < 4 && map[rowEnd].charAt(colEnd + 1) == '1') {
                            colEnd++;
                            area++;
                        }

                        // Expand down
                        while (rowEnd < 4) {
                            int tempColEnd = colEnd;
                            while (tempColEnd >= col && map[rowEnd + 1].charAt(tempColEnd) == '1') {
                                tempColEnd--;
                                area++;
                            }
                            rowEnd++;
                        }

                        maxArea = Math.max(maxArea, area);
                    }
                }
            }

            System.out.println(maxArea);
        }
    }
}
