def max_rectangle(map_str):
    map_list = [list(map(int, row.split())) for row in map_str.split('\n')]
    max_area = 0
    for i in range(len(map_list)):
        for j in range(len(map_list[0])):
            if map_list[i][j] == 1:
                area = 1
                map_list[i][j] = 0
                for k in range(i, -1, -1):
                    if map_list[k][j] == 1:
                        area += 1
                        map_list[k][j] = 0
                for k in range(j, -1, -1):
                    if map_list[i][k] == 1:
                        area *= 2
                        map_list[i][k] = 0
                max_area = max(max_area, area)
    return max_area

n = int(input())
for _ in range(n):
    input()
    map_str = input()
    while map_str:
        map_str = input() + '\n' + map_str
    print(max_rectangle(map_str))
