#include <iostream>
#include <vector>
#include <string>
#include <sstream>

using namespace std;

void printColumn(const string& line, int width, int spacing) {
    int numSpaces = width - line.length();
    for (int i = 0; i < numSpaces; i++) {
        cout << ".";
    }
    cout << line << endl;
}

void printPage(const vector<string>& lines, int numColumns, int width, int spacing) {
    int numLines = lines.size();
    int numRows = (numLines + numColumns - 1) / numColumns;
    for (int i = 0; i < numRows; i++) {
        for (int j = 0; j < numColumns; j++) {
            int index = i * numColumns + j;
            if (index < numLines) {
                string line = lines[index];
                if (line.length() > width) {
                    int start = 0;
                    while (start < line.length() && start < width) {
                        int end = start + width - 1;
                        if (end >= line.length()) {
                            end = line.length() - 1;
                        }
                        printColumn(line.substr(start, end - start + 1), width, spacing);
                        start = end + 1;
                    }
                } else {
                    printColumn(line, width, spacing);
                }
            } else {
                for (int k = 0; k < width; k++) {
                    cout << ".";
                }
            }
            if (j < numColumns - 1) {
                for (int k = 0; k < spacing; k++) {
                    cout << ".";
                }
            }
        }
        cout << endl;
    }
    cout << "#" << endl;
}

int main() {
    string line;
    int plen, cnum, width, cspace;
    while (true) {
        getline(cin, line);
        if (line == "0") {
            break;
        }
        istringstream iss(line);
        iss >> plen >> cnum >> width >> cspace;
        vector<string> lines;
        while (true) {
            getline(cin, line);
            if (line == "?") {
                break;
            }
            lines.push_back(line);
        }
        for (int i = 0; i < plen; i++) {
            if (i < lines.size()) {
                printPage({lines[i]}, cnum, width, cspace);
            } else {
                for (int j = 0; j < cnum; j++) {
                    for (int k = 0; k < width; k++) {
                        cout << ".";
                    }
                    if (j < cnum - 1) {
                        for (int k = 0; k < cspace; k++) {
                            cout << ".";
                        }
                    }
                }
                cout << endl;
            }
            cout << "?" << endl;
        }
    }
    return 0;
}
