import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        StringBuilder sb = new StringBuilder();
        while (true) {
            int plen = scanner.nextInt();
            int cnum = scanner.nextInt();
            int width = scanner.nextInt();
            int cspace = scanner.nextInt();
            scanner.nextLine(); // Consume newline left-over
            String text = "";
            while (true) {
                String line = scanner.nextLine();
                if (line.equals("?")) {
                    break;
                }
                text += line + "\n";
            }
            if (text.trim().isEmpty()) {
                continue;
            }
            int lines = 0;
            String[] linesArray = text.split("\n");
            for (String line : linesArray) {
                if (!line.isEmpty()) {
                    lines++;
                }
            }
            int pages = (lines + cnum - 1) / cnum;
            for (int i = 0; i < pages; i++) {
                int start = i * cnum;
                int end = Math.min(start + cnum, lines);
                for (int j = start; j < end; j++) {
                    String line = linesArray[j];
                    if (line.isEmpty()) {
                        sb.append(new String(new char[width]).replace('\0', '.')).append("\n");
                    } else {
                        sb.append(line.substring(0, width)).append(new String(new char[width - line.length()]).replace('\0', '.')).append("\n");
                    }
                }
                for (int j = 0; j < cnum - 1; j++) {
                    sb.append(new String(new char[cspace]).replace('\0', '.'));
                }
                sb.append("#\n");
                if (i == pages - 1) {
                    sb.append("?\n");
                }
            }
            System.out.print(sb.toString());
            sb.setLength(0);
        }
    }
}
