def print_multi_column(plen, cnum, width, cspace, lines):
    page = []
    current_line = ''
    for line in lines:
        if line:
            if len(current_line) + len(line) + cspace > width:
                page.append(current_line)
                current_line = ''
            current_line += line.ljust(width) + '.' * cspace
        else:
            if current_line:
                page.append(current_line)
                current_line = ''
            page.append('.' * width)
    if current_line:
        page.append(current_line)
    page.append('#')
    return '\n'.join(page[:cnum])

def main():
    while True:
        plen = int(input())
        cnum = int(input())
        width = int(input())
        cspace = int(input())
        lines = []
        while True:
            line = input()
            if line == '?':
                break
            lines.append(line)
        if lines:
            print(print_multi_column(plen, cnum, width, cspace, lines))
            print('?')
        else:
            break

if __name__ == "__main__":
    main()
