#include <stdio.h>

int main() {
    int x = 0, y = 0;
    int d, t;

    while (1) {
        scanf("%d, %d", &d, &t);
        if (d == 0 && t == 0) break;

        x += d * (t < 0 ? -1 : 1);
        y += d * (t > 0 ? 1 : -1);
    }

    printf("%d\n%d\n", x, y);

    return 0;
}
