#include <stdio.h>
#include <string.h>

#define MAX_N 100
#define MAX_P 9
#define MAX_S 4
#define MAX_C 44

int count_solutions(char *puzzle) {
    int count = 0;
    int pieces[MAX_P][MAX_S];
    int used[MAX_P] = {0};
    int rotations[MAX_P][MAX_S] = {{0}};

    char *token = strtok(puzzle, " ");
    for (int i = 0; i < MAX_P; i++) {
        for (int j = 0; j < MAX_S; j++) {
            pieces[i][j] = token[j] - 'a';
        }
        token = strtok(NULL, " ");
    }

    for (int i = 0; i < MAX_P; i++) {
        for (int j = 0; j < MAX_P; j++) {
            if (i != j) {
                int match = 1;
                for (int k = 0; k < MAX_S; k++) {
                    if (pieces[i][k] != pieces[j][k]) {
                        match = 0;
                        break;
                    }
                }
                if (match) {
                    return 0;
                }
            }
        }
    }

    for (int i = 0; i < MAX_P; i++) {
        if (used[i]) {
            continue;
        }
        used[i] = 1;
        for (int j = 0; j < MAX_S; j++) {
            rotations[i][j] = pieces[i][j];
        }
        for (int j = 0; j < 3; j++) {
            for (int k = 0; k < MAX_S; k++) {
                int temp = rotations[i][k];
                rotations[i][k] = rotations[i][(k + 1) % MAX_S];
                rotations[i][(k + 1) % MAX_S] = temp;
            }
            int match = 1;
            for (int k = 0; k < MAX_P; k++) {
                if (k != i) {
                    match = 0;
                    for (int l = 0; l < MAX_S; l++) {
                        if (rotations[i][l] != pieces[k][l]) {
                            match = 0;
                            break;
                        }
                    }
                    if (match) {
                        break;
                    }
                }
            }
            if (match) {
                count++;
            }
        }
    }

    return count;
}

int main() {
    int n;
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        char puzzle[MAX_C];
        scanf("%s", puzzle);
        printf("%d\n", count_solutions(puzzle));
    }
    return 0;
}
