import java.util.Scanner;

public class JigsawPuzzle {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        scanner.nextLine(); // Consume newline left-over

        for (int i = 0; i < n; i++) {
            String puzzle = scanner.nextLine();
            System.out.println(countSolutions(puzzle));
        }
    }

    public static int countSolutions(String puzzle) {
        String[] pieces = puzzle.split(" ");
        int[] counts = new int[8];

        for (String piece : pieces) {
            int index = 0;
            for (int i = 0; i < 4; i++) {
                switch (piece.charAt(i)) {
                    case 'R':
                    case 'r':
                        index |= 1 << 0;
                        break;
                    case 'G':
                    case 'g':
                        index |= 1 << 1;
                        break;
                    case 'B':
                    case 'b':
                        index |= 1 << 2;
                        break;
                    case 'W':
                    case 'w':
                        index |= 1 << 3;
                        break;
                }
            }
            counts[index]++;
        }

        int solutions = 1;
        for (int count : counts) {
            solutions *= count;
        }

        return solutions / 4;
    }
}
