import itertools
import re

def is_rotationally_equal(piece1, piece2):
    return sorted(piece1) == sorted(piece2)

def is_rotationally_symmetric(piece):
    return is_rotationally_equal(piece, piece[::-1])

def count_solutions(puzzle):
    pieces = re.findall(r'\w\w\w\w', puzzle)
    if len(set(pieces)) != len(pieces) or any(is_rotationally_equal(piece1, piece2) for piece1, piece2 in itertools.combinations(pieces, 2)) or any(is_rotationally_symmetric(piece) for piece in pieces):
        return 0
    solutions = set()
    for rotation in itertools.permutations(pieces):
        solution = ''.join(rotation)
        solutions.add(solution)
        for _ in range(3):
            solution = solution[1:] + solution[0]
            solutions.add(solution)
    return len(solutions) // 4

def main():
    n = int(input())
    for _ in range(n):
        puzzle = input()
        print(count_solutions(puzzle))

if __name__ == "__main__":
    main()
