#include <stdio.h>
#include <stdlib.h>

#define MAX_PRODUCTS 100
#define MAX_STORES 10

int solve(int p, int s, char **rows) {
    int i, j, k;
    int *sales = (int *)malloc((p + 1) * sizeof(int));
    int *store_sales = (int *)malloc(s * sizeof(int));
    int *product_sales = (int *)malloc((p + 1) * sizeof(int));

    for (i = 0; i <= p; i++) {
        sales[i] = 0;
    }

    for (i = 0; i < s; i++) {
        store_sales[i] = 0;
    }

    for (i = 0; i <= p; i++) {
        product_sales[i] = 0;
    }

    for (i = 0; i <= p; i++) {
        char *row = rows[i];
        int j = 0;
        while (row[j] != ' ') {
            j++;
        }
        j++;
        while (row[j] != ' ') {
            int num;
            if (row[j] == '?') {
                num = -1;
            } else {
                num = atoi(&row[j]);
            }
            sales[i] += num;
            j += strlen(&row[j]) + 1;
        }
    }

    for (i = 0; i < s; i++) {
        char *row = rows[p + 1];
        int j = 0;
        while (row[j] != ' ') {
            j++;
        }
        j++;
        while (row[j] != ' ') {
            int num;
            if (row[j] == '?') {
                num = -1;
            } else {
                num = atoi(&row[j]);
            }
            store_sales[i] += num;
            j += strlen(&row[j]) + 1;
        }
    }

    for (i = 0; i <= p; i++) {
        product_sales[i] = sales[i];
    }

    for (i = 0; i < s; i++) {
        for (j = 0; j <= p; j++) {
            if (rows[j][s * (i + 1) + 1] == '?') {
                int x = sales[j] - product_sales[j];
                int y = store_sales[i] - x;
                if (y < 0) {
                    return 0;
                }
                rows[j][s * (i + 1) + 1] = '0' + y / 10;
                rows[j][s * (i + 1) + 2] = '0' + y % 10;
            }
        }
    }

    for (i = 0; i <= p; i++) {
        for (j = 0; j < s; j++) {
            if (rows[i][j + 1] == '?') {
                int x = sales[i] - product_sales[i];
                int y = store_sales[j] - x;
                if (y < 0) {
                    return 0;
                }
                rows[i][j + 1] = '0' + y / 10;
                rows[i][j + 2] = '0' + y % 10;
            }
        }
    }

    for (i = 0; i <= p; i++) {
        for (j = 0; j < s; j++) {
            if (rows[i][j + 1] == '?') {
                return 0;
            }
        }
    }

    for (i = 0; i < s; i++) {
        for (j = 0; j <= p; j++) {
            if (rows[j][s * (i + 1) + 1] == '?') {
                return 0;
            }
        }
    }

    for (i = 0; i <= p; i++) {
        for (j = 0; j < s; j++) {
            printf("%c", rows[i][j + 1]);
        }
        printf("\n");
    }

    free(sales);
    free(store_sales);
    free(product_sales);
    return 1;
}

