#include <iostream>
#include <vector>
#include <string>
#include <sstream>

using namespace std;

int main() {
    int p, s;
    while (cin >> p >> s) {
        if (p == 0 && s == 0) break;

        vector<vector<int>> table(p + 1, vector<int>(s + 1));
        for (int i = 0; i <= p; i++) {
            string line;
            getline(cin, line);
            istringstream iss(line);
            for (int j = 0; j <= s; j++) {
                string num;
                iss >> num;
                if (num == "?") table[i][j] = -1;
                else table[i][j] = stoi(num);
            }
        }

        bool hasSolution = true;
        for (int i = 0; i < p; i++) {
            for (int j = 0; j < s; j++) {
                if (table[i][j] == -1) {
                    int productTotal = table[i][s];
                    int storeTotal = table[s][j];
                    int missing = productTotal - storeTotal + table[i][j];
                    if (missing < -100000 || missing > 100000) {
                        hasSolution = false;
                        break;
                    }
                    table[i][j] = missing;
                }
            }
            if (!hasSolution) break;
        }

        for (int i = 0; i < p; i++) {
            for (int j = 0; j < s; j++) {
                if (table[i][j] == -1) {
                    hasSolution = false;
                    break;
                }
            }
            if (!hasSolution) break;
        }

        if (hasSolution) {
            for (int i = 0; i < p; i++) {
                for (int j = 0; j < s; j++) {
                    if (table[i][j] == -1) {
                        int productTotal = table[i][s];
                        int storeTotal = table[s][j];
                        int missing = productTotal - storeTotal + table[i][j];
                        cout << missing << endl;
                    }
                }
            }
        } else {
            cout << "NO" << endl;
        }

        cout << endl;
    }

    return 0;
}
