#include <stdio.h>
#include <stdbool.h>

bool is_proper_net(int mesh[5][5]) {
    int opposite_faces[6][2] = {{1, 6}, {2, 5}, {3, 4}, {4, 3}, {5, 2}, {6, 1}};
    int i, j, k, l;

    // Check if the sum of two numbers on opposite faces is always 7
    for (i = 0; i < 6; i++) {
        for (j = 0; j < 5; j++) {
            for (k = 0; k < 5; k++) {
                if (mesh[j][k] == opposite_faces[i][0] && mesh[4 - j][4 - k] == opposite_faces[i][1]) {
                    for (l = 0; l < 5; l++) {
                        if (l != k && mesh[j][l] != 0 && mesh[j][l] != opposite_faces[i][0]) {
                            return false;
                        }
                    }
                    for (l = 0; l < 5; l++) {
                        if (l != k && mesh[4 - j][l] != 0 && mesh[4 - j][l] != opposite_faces[i][1]) {
                            return false;
                        }
                    }
                }
            }
        }
    }

    // Check if some faces are marked with the same number
    for (i = 0; i < 5; i++) {
        for (j = 0; j < 5; j++) {
            if (mesh[i][j] != 0 && mesh[i][j] == mesh[i][j + 1]) {
                return false;
            }
            if (mesh[i][j] != 0 && mesh[i][j] == mesh[i + 1][j]) {
                return false;
            }
        }
    }

    // Check if the plane shape is cut off into two parts
    for (i = 0; i < 5; i++) {
        for (j = 0; j < 5; j++) {
            if (mesh[i][j] == 0 && (i == 0 || i == 4 || j == 0 || j == 4)) {
                return false;
            }
        }
    }

    return true;
}

int main() {
    int N;
    scanf("%d", &N);
    int i, j, k;
    for (i = 0; i < N; i++) {
        int mesh[5][5];
        for (j = 0; j < 5; j++) {
            for (k = 0; k < 5; k++) {
                scanf("%d", &mesh[j][k]);
            }
        }
        printf("%s\n", is_proper_net(mesh) ? "true" : "false");
    }
    return 0;
}
