#include <iostream>
#include <vector>
#include <string>
#include <sstream>

bool isProperNet(const std::vector<std::vector<int>>& mesh) {
    // Check if the sum of two numbers on opposite faces is always 7
    for (int i = 0; i < 3; ++i) {
        int sum = mesh[i][1] + mesh[3 - i][4];
        if (sum != 7) {
            return false;
        }
    }

    // Check if some faces are marked with the same number
    for (int i = 0; i < 5; ++i) {
        for (int j = 0; j < 5; ++j) {
            if (i != j && mesh[i / 5][i % 5] == mesh[j / 5][j % 5]) {
                return false;
            }
        }
    }

    // Check if the plane shape is cut off into two parts
    for (int i = 0; i < 5; ++i) {
        for (int j = 0; j < 5; ++j) {
            if (mesh[i][j] == 0 && (i == 0 || i == 4 || j == 0 || j == 4)) {
                return false;
            }
        }
    }

    // Check if there is an extra face marked with a number
    for (int i = 0; i < 5; ++i) {
        for (int j = 0; j < 5; ++j) {
            if (mesh[i][j] != 0 && (i == 0 || i == 4 || j == 0 || j == 4)) {
                return false;
            }
        }
    }

    return true;
}

int main() {
    int n;
    std::cin >> n;

    for (int i = 0; i < n; ++i) {
        std::vector<std::vector<int>> mesh(5, std::vector<int>(5));
        for (int j = 0; j < 5; ++j) {
            for (int k = 0; k < 5; ++k) {
                std::cin >> mesh[j][k];
            }
        }

        std::cout << (isProperNet(mesh) ? "true" : "false") << std::endl;
    }

    return 0;
}
