def is_proper_net(mesh):
    # Check if the sum of opposite faces is always 7
    for i in range(5):
        if mesh[i][0] + mesh[i][4] != 7:
            return False
        if mesh[0][i] + mesh[4][i] != 7:
            return False

    # Check if all faces have different numbers
    for i in range(5):
        for j in range(5):
            if mesh[i][j] == 0:
                continue
            for k in range(5):
                for l in range(5):
                    if mesh[k][l] == mesh[i][j] and (k, l) != (i, j):
                        return False

    # Check if the mesh is a valid representation of a cube
    for i in range(5):
        for j in range(5):
            if mesh[i][j] == 0 and (i, j) not in [(0, 0), (0, 4), (4, 0), (4, 4)]:
                return False

    return True


def main():
    N = int(input())
    for _ in range(N):
        mesh = [list(map(int, input().split())) for _ in range(5)]
        print(is_proper_net(mesh))


if __name__ == "__main__":
    main()
