#include <iostream>
#include <cmath>
#include <vector>
#include <algorithm>

struct Point {
    int x, y;
};

bool comparePoints(const Point& a, const Point& b) {
    double distanceA = std::sqrt(a.x * a.x + a.y * a.y);
    double distanceB = std::sqrt(b.x * b.x + b.y * b.y);
    if (distanceA == distanceB) {
        return a.x > b.x;
    }
    return distanceA > distanceB;
}

int main() {
    int n;
    std::cin >> n;

    std::vector<Point> points;
    for (int i = 0; i < n; ++i) {
        int dx, dy;
        std::cin >> dx >> dy;
        while (dx != 0 || dy != 0) {
            points.push_back({points.back().x + dx, points.back().y + dy});
            std::cin >> dx >> dy;
        }
    }

    for (int i = 0; i < n; ++i) {
        std::sort(points.begin() + i * 2, points.begin() + (i + 1) * 2, comparePoints);
        std::cout << points[i * 2 + 1].x << " " << points[i * 2 + 1].y << std::endl;
    }

    return 0;
}
