#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

int main() {
    int t;
    cin >> t;
    while (t--) {
        int n;
        cin >> n;
        vector<vector<int>> instructions(n);
        for (int i = 0; i < n; i++) {
            int a, b;
            cin >> a >> b;
            instructions[i] = {a, b};
        }

        vector<vector<int>> piles(n + 1);
        for (int i = 0; i <= n; i++) {
            piles[i].push_back(i);
        }

        for (int i = 0; i < n; i++) {
            int a = instructions[i][0];
            int b = instructions[i][1];

            if (b == 0) {
                for (int j = 0; j < piles[a].size(); j++) {
                    piles[0].push_back(piles[a][j]);
                }
                piles[a].clear();
            } else {
                int x = upper_bound(piles[b].begin(), piles[b].end(), a) - piles[b].begin();
                if (x < piles[b].size() && piles[b][x] == a) {
                    for (int j = x + 1; j < piles[b].size(); j++) {
                        piles[0].push_back(piles[b][j]);
                    }
                    piles[b].erase(piles[b].begin() + x);
                } else {
                    piles[b].push_back(a);
                }
            }
        }

        sort(piles.begin() + 1, piles.end(), [](const vector<int>& a, const vector<int>& b) {
            return a[0] < b[0];
        });

        for (int i = 1; i <= n; i++) {
            cout << piles[i].size() << endl;
        }
        cout << "end" << endl;
    }

    return 0;
}
