import java.util.*;
import java.io.*;

public class Main {
    static class Pair {
        int first, second;
        Pair(int first, int second) {
            this.first = first;
            this.second = second;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int t = 1;
        while (t-- > 0) {
            int n = Integer.parseInt(br.readLine());
            List<List<Integer>> piles = new ArrayList<>();
            for (int i = 0; i <= n; i++) {
                piles.add(new ArrayList<>());
            }
            while (true) {
                String[] line = br.readLine().split(" ");
                if (line[0].equals("0") && line[1].equals("0")) break;
                int i = Integer.parseInt(line[0]);
                int j = Integer.parseInt(line[1]);
                if (j == 0) {
                    while (piles.get(i).size() > 0) {
                        piles.get(0).addAll(piles.get(i));
                        piles.get(i).clear();
                    }
                } else {
                    int k = findParent(piles, j);
                    if (k == i) {
                        if (piles.get(i).size() > piles.get(j).size()) {
                            int temp = piles.get(i).size();
                            piles.get(i).clear();
                            piles.get(i).addAll(piles.get(j));
                            piles.get(j).clear();
                            piles.get(j).addAll(piles.get(k));
                            piles.get(k).clear();
                            piles.get(k).addAll(piles.get(i));
                            piles.get(i).clear();
                            piles.get(i).add(piles.get(k).size() - temp);
                        }
                    } else {
                        if (k < i) {
                            int temp = piles.get(i).size();
                            piles.get(i).clear();
                            piles.get(i).addAll(piles.get(k));
                            piles.get(k).clear();
                            piles.get(k).addAll(piles.get(j));
                            piles.get(j).clear();
                            piles.get(j).add(piles.get(k).size() - temp);
                        }
                    }
                }
            }
            List<Integer> heights = new ArrayList<>();
            for (int i = 1; i <= n; i++) {
                heights.add(piles.get(i).size());
            }
            Collections.sort(heights);
            for (int height : heights) {
                System.out.println(height);
            }
            System.out.println("end");
        }
    }

    static int findParent(List<List<Integer>> piles, int i) {
        if (piles.get(i).size() == 0) return i;
        return findParent(piles, piles.get(i).get(0));
    }
}
