#include <iostream>
#include <string>
#include <map>
#include <vector>

int main() {
    int n;
    std::cin >> n;
    std::cin.ignore();

    std::map<std::string, int> charCount;
    std::map<std::string, std::map<std::string, int>> pairCount;

    for (int i = 0; i < n; i++) {
        std::string line;
        std::getline(std::cin, line);

        for (int j = 0; j < line.length() - 1; j++) {
            std::string char1 = line.substr(j, 1);
            std::string char2 = line.substr(j + 1, 1);

            if (charCount.find(char1) == charCount.end()) {
                charCount[char1] = 0;
            }

            if (pairCount.find(char1) == pairCount.end()) {
                pairCount[char1] = {};
            }

            if (pairCount[char1].find(char2) == pairCount[char1].end()) {
                pairCount[char1][char2] = 0;
            }

            charCount[char1]++;
            pairCount[char1][char2]++;
        }
    }

    for (int i = 1; i <= 26; i++) {
        std::string char1 = "a" + std::to_string(i);
        if (charCount.find(char1) != charCount.end()) {
            std::string maxChar = "a";
            int maxCount = 0;
            for (auto& pair : pairCount[char1]) {
                if (pair.second > maxCount) {
                    maxChar = pair.first;
                    maxCount = pair.second;
                }
            }
            std::cout << char1 << " " << maxChar << " " << maxCount << std::endl;
        } else {
            std::cout << char1 << " a 0" << std::endl;
        }
    }

    for (int i = 27; i <= 38; i++) {
        std::string char1 = getKanChar(i);
        if (charCount.find(char1) != charCount.end()) {
            std::string maxChar = "a";
            int maxCount = 0;
            for (auto& pair : pairCount[char1]) {
                if (pair.second > maxCount) {
                    maxChar = pair.first;
                    maxCount = pair.second;
                }
            }
            std::cout << char1 << " " << maxChar << " " << maxCount << std::endl;
        } else {
            std::cout << char1 << " a 0" << std::endl;
        }
    }

    return 0;
}

std::string getKanChar(int index) {
    if (index <= 26) {
        return "a" + std::to_string(index);
    } else if (index <= 28) {
        return "ld";
    } else if (index <= 30) {
        return "mb";
    } else if (index <= 32) {
        return "mp";
    } else if (index <= 34) {
        return "nc";
    } else if (index <= 36) {
        return "nd";
    } else if (index <= 38) {
        return "ng";
    }
}
