import java.util.HashMap;
import java.util.Map;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        scanner.nextLine(); // consume newline left-over

        Map<String, Integer> count = new HashMap<>();
        Map<String, Integer> maxCount = new HashMap<>();
        Map<String, Integer> maxKc = new HashMap<>();

        for (int i = 0; i < n; i++) {
            String line = scanner.nextLine();
            String[] words = line.split(" ");

            for (String word : words) {
                StringBuilder sb = new StringBuilder();
                for (int j = 0; j < word.length(); j++) {
                    if (j + 1 < word.length() && isKanCharacter(word.substring(j, j + 2))) {
                        sb.append(word.substring(j, j + 2));
                        j++;
                    } else {
                        sb.append(word.charAt(j));
                    }
                }

                String[] kanChars = sb.toString().split(" ");
                for (int k = 0; k < kanChars.length - 1; k++) {
                    String kc1 = kanChars[k];
                    String kc2 = kanChars[k + 1];
                    String pair = kc1 + " " + kc2;

                    count.put(pair, count.getOrDefault(pair, 0) + 1);
                    maxCount.put(kc1, Math.max(maxCount.getOrDefault(kc1, 0), count.get(pair)));
                    maxKc.put(kc1, kc2);
                }
            }
        }

        for (int i = 1; i <= 38; i++) {
            String kc = getKc(i);
            String maxKcStr = maxKc.get(kc);
            int maxCountVal = maxCount.get(kc);

            System.out.println(kc + " " + (maxKcStr == null ? "a" : maxKcStr) + " " + (maxCountVal == 0 ? 0 : maxCountVal));
        }
    }

    private static boolean isKanCharacter(String s) {
        return s.equals("ld") || s.equals("mb") || s.equals("mp") || s.equals("nc") || s.equals("nd") || s.equals("ng")
                || s.equals("nt") || s.equals("nw") || s.equals("ps") || s.equals("qu") || s.equals("cw") || s.equals("ts");
    }

    private static String getKc(int i) {
        if (i <= 26) {
            return String.valueOf((char) ('a' + i - 1));
        } else {
            return getKc(i - 26);
        }
    }
}
