import re
from collections import defaultdict

def kanglish_analysis(lines):
    kan_chars = "abcdefghijklmnopqrstuvwxyzldmbmpncndngntnwpsqucwtsts"
    kan_pairs = [(kan_chars[i], kan_chars[j]) for i in range(len(kan_chars)) for j in range(len(kan_chars))]
    kan_dict = {kan_chars[i]: i for i in range(len(kan_chars))}
    pair_dict = defaultdict(int)

    for line in lines[1:]:
        words = re.findall(r'\w+', line)
        for word in words:
            i = 0
            while i < len(word) - 1:
                pair = word[i:i+2]
                if pair in kan_dict:
                    pair_dict[(kan_dict[word[i]], kan_dict[pair])] += 1
                i += 1

    for i in range(len(kan_chars)):
        max_count = 0
        most_frequent_pair = None
        for pair in kan_pairs:
            if pair[0] == kan_chars[i] and pair_dict[(i, kan_dict[pair[1]])] > max_count:
                max_count = pair_dict[(i, kan_dict[pair[1]])]
                most_frequent_pair = pair
        print(f"{kan_chars[i]} {most_frequent_pair[1]} {max_count}")

def main():
    n = int(input())
    lines = [input() for _ in range(n)]
    kanglish_analysis(lines)

if __name__ == "__main__":
    main()
