#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_DIGITS 10
#define MAX_HINTS 100

int compare(const void *a, const void *b) {
    return strcmp((char *)a, (char *)b);
}

int main() {
    int n;
    scanf("%d", &n);

    while (n--) {
        int L, H;
        scanf("%d %d", &L, &H);

        if (L == 0 && H == 0) break;

        char **tries = (char **)malloc(H * sizeof(char *));
        for (int i = 0; i < H; i++) {
            tries[i] = (char *)malloc((L + 1) * sizeof(char));
            scanf("%s", tries[i]);
        }

        qsort(tries, H, sizeof(char *), compare);

        char *ans = (char *)malloc((L + 1) * sizeof(char));
        memset(ans, '0', L + 1);
        ans[L] = '\0';

        int hit = 0;
        int blow = 0;
        int i = 0;
        int j = 0;

        while (i < H && j < H) {
            if (strcmp(tries[i], tries[j]) == 0) {
                int k = 0;
                while (k < L && ans[k] != '\0') k++;
                if (k < L) {
                    ans[k] = tries[j][0];
                    k++;
                }
                hit = 0;
                blow = 0;
                for (int l = 0; l < L; l++) {
                    if (tries[i][l] == ans[l]) hit++;
                    else if (tries[i][l] != '\0') blow++;
                }
                if (hit == L) break;
                i++;
                j++;
            } else if (strcmp(tries[i], tries[j]) < 0) {
                i++;
            } else {
                j++;
            }
        }

        if (hit == L) {
            printf("%s\n", ans);
        } else {
            printf("NO\n");
        }

        for (int i = 0; i < H; i++) {
            free(tries[i]);
        }
        free(tries);
    }

    return 0;
}
