#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    vector<string> ans;

    string line;
    while (getline(cin, line)) {
        if (line == "0 0") break;

        int L, H;
        sscanf(line.c_str(), "%d %d", &L, &H);

        vector<string> tries(H);
        for (int i = 0; i < H; i++) {
            getline(cin, line);
            tries[i] = line;
        }

        vector<int> hits(H), blows(H);
        for (int i = 0; i < H; i++) {
            sscanf(tries[i].c_str(), "%*s %d %d", &hits[i], &blows[i]);
        }

        vector<int> digits(10);
        for (int i = 0; i < H; i++) {
            for (char c : tries[i]) {
                digits[c - '0']++;
            }
        }

        vector<int> used(L);
        for (int i = 0; i < L; i++) {
            used[i] = -1;
        }

        string res(L, '0');
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < L; j++) {
                if (tries[i][j] != res[j] && hits[i] > 0) {
                    for (int k = 0; k < 10; k++) {
                        if (digits[k] > 0 && k != tries[i][j] - '0') {
                            res[j] = '0' + k;
                            digits[k]--;
                            break;
                        }
                    }
                }
            }

            for (int j = 0; j < L; j++) {
                if (res[j] == tries[i][j]) {
                    used[j] = 1;
                }
            }

            for (int j = 0; j < L; j++) {
                if (used[j] == 1) {
                    used[j] = 0;
                } else {
                    for (int k = 0; k < 10; k++) {
                        if (digits[k] > 0 && k != tries[i][j] - '0') {
                            res[j] = '0' + k;
                            digits[k]--;
                            break;
                        }
                    }
                }
            }
        }

        bool valid = true;
        for (int i = 0; i < H; i++) {
            int hit = 0, blow = 0;
            for (int j = 0; j < L; j++) {
                if (res[j] == tries[i][j]) {
                    hit++;
                } else if (used[j] == 1) {
                    blow++;
                }
            }
            if (hit != hits[i] || blow != blows[i]) {
                valid = false;
                break;
            }
        }

        if (valid) {
            ans.push_back(res);
        } else {
            ans.push_back("NO");
        }
    }

    for (const auto& a : ans) {
        cout << a << endl;
    }

    return 0;
}
