import itertools

def guess_number(hints):
    L = int(hints[0].split()[0])
    H = int(hints[0].split()[1])
    hints = hints[1:]

    def is_valid(num):
        return len(num) == L and len(set(num)) == L

    def count_hit(num, hint):
        return sum(1 for a, b in zip(num, hint['try']) if a == b)

    def count_blow(num, hint):
        return sum(1 for a in num if a in hint['try'] and a != hint['try'][num.index(a)])

    def check(num, hints):
        for hint in hints:
            if count_hit(num, hint) != hint['hit'] or count_blow(num, hint) != hint['blow']:
                return False
        return True

    def solve(hints):
        for num in itertools.permutations('0123456789', L):
            num = ''.join(num)
            if is_valid(num) and check(num, hints):
                return num
        return 'NO'

    return solve(hints)

def main():
    hints = []
    while True:
        line = input()
        if line == '0 0':
            break
        hints.append(line)
    for hint in hints:
        print(guess_number(hint.split('\n')))

if __name__ == '__main__':
    main()
