#include <stdio.h>
#include <math.h>

#define MAX_CIRCLES 100

typedef struct {
    double x, y, r;
} Circle;

double distance(Circle c1, Circle c2) {
    return sqrt(pow(c1.x - c2.x, 2) + pow(c1.y - c2.y, 2));
}

double min_distance(Circle c1, Circle c2, Circle c3) {
    double d12 = distance(c1, c2);
    double d23 = distance(c2, c3);
    double d13 = distance(c1, c3);
    return min(d12, d23, d13);
}

double rope_length(Circle circles[], int n) {
    double total_length = 0;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            double d = min_distance(circles[i], circles[j], circles[(j + 1) % n]);
            total_length += d;
        }
    }
    return total_length;
}

int main() {
    int n;
    while (scanf("%d", &n) == 1) {
        if (n == 0) break;
        Circle circles[MAX_CIRCLES];
        for (int i = 0; i < n; i++) {
            scanf("%lf %lf %lf", &circles[i].x, &circles[i].y, &circles[i].r);
        }
        double total_length = rope_length(circles, n);
        printf("%.5f\n", total_length);
    }
    return 0;
}
