#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

struct Circle {
    double x, y, r;
};

double distance(const Circle& a, const Circle& b) {
    return std::sqrt(std::pow(a.x - b.x, 2) + std::pow(a.y - b.y, 2));
}

double ropeLength(const std::vector<Circle>& circles) {
    double totalLength = 0;
    std::vector<Circle> sortedCircles = circles;
    std::sort(sortedCircles.begin(), sortedCircles.end(), [](const Circle& a, const Circle& b) {
        return a.r < b.r;
    });

    for (int i = 0; i < sortedCircles.size() - 1; ++i) {
        totalLength += 2 * std::asin(distance(sortedCircles[i], sortedCircles[i + 1]) / (sortedCircles[i].r + sortedCircles[i + 1].r));
    }

    return totalLength * sortedCircles.back().r;
}

int main() {
    int n;
    while (std::cin >> n) {
        if (n == 0) break;
        std::vector<Circle> circles(n);
        for (int i = 0; i < n; ++i) {
            std::cin >> circles[i].x >> circles[i].y >> circles[i].r;
        }
        std::cout << std::fixed << std::setprecision(5) << ropeLength(circles) << std::endl;
    }
    return 0;
}
