import math

def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

def min_distance(c1, c2):
    return max(abs(c1[0] - c2[0]), abs(c1[1] - c2[1])) - c1[2] - c2[2]

def rope_length(circles):
    circles.sort(key=lambda x: x[2], reverse=True)
    rope = [circles[0]]
    for i in range(1, len(circles)):
        if min_distance(rope[-1], circles[i]) > 0:
            rope.append(circles[i])
    rope.sort(key=lambda x: x[0])
    total_length = 0
    for i in range(len(rope) - 1):
        total_length += distance(rope[i], rope[i+1])
    return total_length

def main():
    while True:
        n = int(input())
        if n == 0:
            break
        circles = []
        for _ in range(n):
            x, y, r = map(float, input().split())
            circles.append([x, y, r])
        print(f"{rope_length(circles):.5f}")

if __name__ == "__main__":
    main()
