#include <stdio.h>
#include <stdlib.h>

#define MAX_MEMBERS 50
#define MAX_DATES 100

int count_dates(int dates[], int n) {
    int count = 0;
    for (int i = 0; i < n; i++) {
        if (dates[i] > 0) {
            count++;
        }
    }
    return count;
}

int main() {
    int n, q, m, i, j, k, dates[MAX_MEMBERS][MAX_DATES], count[MAX_DATES] = {0};
    int max_count = 0, max_date = 0;

    while (scanf("%d %d", &n, &q) == 2) {
        for (i = 0; i < n; i++) {
            scanf("%d", &m);
            for (j = 0; j < m; j++) {
                scanf("%d", &dates[i][j]);
            }
        }

        for (i = 0; i < n; i++) {
            for (j = 0; j < MAX_DATES; j++) {
                if (dates[i][j] > 0) {
                    count[j]++;
                }
            }
        }

        for (i = 0; i < MAX_DATES; i++) {
            if (count[i] >= q && count[i] > max_count) {
                max_count = count[i];
                max_date = i;
            } else if (count[i] >= q && count[i] == max_count && i < max_date) {
                max_date = i;
            }
        }

        if (max_count < q) {
            printf("0\n");
        } else {
            printf("%d\n", max_date + 1);
        }

        for (i = 0; i < n; i++) {
            for (j = 0; j < MAX_DATES; j++) {
                dates[i][j] = 0;
            }
        }

        for (i = 0; i < MAX_DATES; i++) {
            count[i] = 0;
        }

        max_count = 0;
        max_date = 0;
    }

    return 0;
}
