#include <iostream>
#include <vector>
#include <map>
#include <algorithm>

using namespace std;

int main() {
    int n, q;
    while (cin >> n >> q) {
        map<int, int> dates;
        for (int i = 0; i < n; ++i) {
            int m;
            cin >> m;
            for (int j = 0; j < m; ++j) {
                int date;
                cin >> date;
                dates[date]++;
            }
        }
        int maxCount = 0;
        int result = 0;
        for (auto& pair : dates) {
            if (pair.second >= q && pair.first < result || pair.second >= q && result == 0) {
                maxCount = pair.second;
                result = pair.first;
            }
        }
        cout << (maxCount >= q ? result : 0) << endl;
        while (cin.peek() == '\n') {
            cin.ignore();
            cin >> n >> q;
        }
    }
    return 0;
}
