#include <stdio.h>

#define MAX_N 500
#define MAX_W 100
#define MAX_H 100

int main() {
    int n, w, h, s, t, i, j, k, x, y, count, max_count;
    int field[MAX_W][MAX_H];

    while (scanf("%d", &n) == 1) {
        if (n == 0) break;

        scanf("%d %d", &w, &h);
        for (i = 0; i < w; i++) {
            for (j = 0; j < h; j++) {
                field[i][j] = 0;
            }
        }

        for (i = 0; i < n; i++) {
            scanf("%d %d", &x, &y);
            field[x - 1][y - 1] = 1;
        }

        scanf("%d %d", &s, &t);

        max_count = 0;
        for (i = 0; i <= w - s; i++) {
            for (j = 0; j <= h - t; j++) {
                count = 0;
                for (k = i; k < i + s; k++) {
                    for (k2 = j; k2 < j + t; k2++) {
                        count += field[k][k2];
                    }
                }
                if (count > max_count) {
                    max_count = count;
                }
            }
        }

        printf("%d\n", max_count);
    }

    return 0;
}
