#include <iostream>
#include <vector>
#include <algorithm>

int countPersimmonTrees(const std::vector<std::pair<int, int>>& trees, int width, int height) {
    int count = 0;
    for (const auto& tree : trees) {
        if (tree.first >= width || tree.second >= height) {
            continue;
        }
        count++;
    }
    return count;
}

int main() {
    int n, w, h, s, t;
    while (std::cin >> n >> w >> h >> s >> t) {
        std::vector<std::pair<int, int>> trees(n);
        for (auto& tree : trees) {
            std::cin >> tree.first >> tree.second;
        }
        int maxCount = 0;
        for (int i = 0; i <= w - s; i++) {
            for (int j = 0; j <= h - t; j++) {
                int count = countPersimmonTrees(trees, i + s, j + t);
                maxCount = std::max(maxCount, count);
            }
        }
        std::cout << maxCount << std::endl;
        if (std::cin.peek() == '\n') {
            std::cin.ignore();
        }
    }
    return 0;
}
