def get_max_persimmon_trees():
    while True:
        try:
            n = int(input())
            w, h = map(int, input().split())
            trees = []
            for _ in range(n):
                x, y = map(int, input().split())
                trees.append((x, y))
            s, t = map(int, input().split())

            max_trees = 0
            for i in range(w - s + 1):
                for j in range(h - t + 1):
                    count = 0
                    for x, y in trees:
                        if i <= x - 1 < i + s and j <= y - 1 < j + t:
                            count += 1
                    max_trees = max(max_trees, count)

            print(max_trees)
        except ValueError:
            break

