#include <stdio.h>
#include <string.h>

#define MAX_LENGTH 80
#define MAX_DATA 20

int find_shift(char *text) {
    for (int shift = 0; shift < 26; shift++) {
        char decoded_text[MAX_LENGTH + 1];
        for (int i = 0; i < strlen(text); i++) {
            if (text[i] >= 'a' && text[i] <= 'z') {
                decoded_text[i] = 'a' + (text[i] - 'a' - shift + 26) % 26;
            } else {
                decoded_text[i] = text[i];
            }
        }
        decoded_text[strlen(text)] = '\0';
        if (strstr(decoded_text, "the") || strstr(decoded_text, "this") || strstr(decoded_text, "that")) {
            return shift;
        }
    }
    return -1;
}

int main() {
    char text[MAX_LENGTH + 1];
    for (int i = 0; i < MAX_DATA; i++) {
        fgets(text, MAX_LENGTH + 1, stdin);
        text[strlen(text) - 1] = '\0'; // remove newline character
        int shift = find_shift(text);
        if (shift != -1) {
            char decoded_text[MAX_LENGTH + 1];
            for (int j = 0; j < strlen(text); j++) {
                if (text[j] >= 'a' && text[j] <= 'z') {
                    decoded_text[j] = 'a' + (text[j] - 'a' - shift + 26) % 26;
                } else {
                    decoded_text[j] = text[j];
                }
            }
            decoded_text[strlen(text)] = '\0';
            printf("%s\n", decoded_text);
        }
    }
    return 0;
}
