#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_W 70
#define MAX_H 70

int compare(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}

int is_valid(int i, int j, int w, int h, char matrix[][MAX_W + 1]) {
    if (i < 0 || i >= h || j < 0 || j >= w) return 0;
    if (matrix[i][j] < '0' || matrix[i][j] > '9') return 0;
    return 1;
}

int find_secret_number(int w, int h, char matrix[][MAX_W + 1]) {
    int dp[h][w];
    int max_num = 0;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            dp[i][j] = 0;
        }
    }

    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (matrix[i][j] >= '0' && matrix[i][j] <= '9') {
                int num = matrix[i][j] - '0';
                if (i > 0 && dp[i - 1][j] > 0) {
                    num = num * 10 + dp[i - 1][j];
                }
                dp[i][j] = num;
                if (num > max_num) max_num = num;
            }
        }
    }

    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (dp[i][j] > 0) {
                int num = dp[i][j];
                if (i > 0 && j > 0 && dp[i - 1][j - 1] > 0) {
                    num = num * 10 + dp[i - 1][j - 1];
                }
                if (i > 0 && dp[i - 1][j] > 0) {
                    num = num * 10 + dp[i - 1][j];
                }
                if (j > 0 && dp[i][j - 1] > 0) {
                    num = num * 10 + dp[i][j - 1];
                }
                if (num > max_num) max_num = num;
            }
        }
    }

    char num_str[20];
    int k = 0;
    while (max_num > 0) {
        num_str[k++] = max_num % 10 + '0';
        max_num /= 10;
    }
    for (int i = k - 1; i >= 0; i--) {
        printf("%c", num_str[i]);
    }
    printf("\n");

    return 0;
}

int main() {
    int w, h;
    char matrix[MAX_H + 1][MAX_W + 1];
    while (scanf("%d %d", &w, &h) == 2) {
        if (w == 0 && h == 0) break;
        for (int i = 0; i < h; i++) {
            scanf("%s", matrix[i]);
        }
        find_secret_number(w, h, matrix);
    }

    return 0;
}
