#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    int w, h;
    while (cin >> w >> h) {
        if (w == 0 && h == 0) break;
        vector<string> matrix(h);
        for (int i = 0; i < h; i++) {
            cin >> matrix[i];
        }
        string secret;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                string num;
                int x = i, y = j;
                while (x < h && y < w) {
                    if (isdigit(matrix[x][y])) {
                        num += matrix[x][y];
                        x++;
                        y++;
                    } else {
                        break;
                    }
                }
                if (num.size() > 1) {
                    sort(num.begin(), num.end());
                    if (secret.size() < num.size() || (secret.size() == num.size() && secret < num)) {
                        secret = num;
                    }
                }
            }
        }
        cout << secret << endl;
    }
    return 0;
}
