import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int w = scanner.nextInt();
            int h = scanner.nextInt();
            if (w == 0 && h == 0) break;
            char[][] matrix = new char[h][w];
            for (int i = 0; i < h; i++) {
                String row = scanner.next();
                for (int j = 0; j < w; j++) {
                    matrix[i][j] = row.charAt(j);
                }
            }
            String secret = findSecret(matrix);
            System.out.println(secret);
        }
    }

    public static String findSecret(char[][] matrix) {
        int h = matrix.length;
        int w = matrix[0].length;
        boolean[][] visited = new boolean[h][w];
        String[] numbers = new String[h * w];
        int index = 0;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                if (Character.isDigit(matrix[i][j])) {
                    dfs(matrix, i, j, visited, numbers, index);
                    index++;
                }
            }
        }
        Arrays.sort(numbers);
        return numbers[numbers.length - 1];
    }

    public static void dfs(char[][] matrix, int i, int j, boolean[][] visited, String[] numbers, int index) {
        if (i < 0 || i >= matrix.length || j < 0 || j >= matrix[0].length || visited[i][j] || !Character.isDigit(matrix[i][j])) return;
        visited[i][j] = true;
        StringBuilder num = new StringBuilder();
        num.append(matrix[i][j]);
        int x = i;
        int y = j;
        while (x > 0 && y > 0 && x < matrix.length - 1 && y < matrix[0].length - 1) {
            if (Character.isDigit(matrix[x - 1][y]) && Character.isDigit(matrix[x + 1][y]) && Character.isDigit(matrix[x][y - 1]) && Character.isDigit(matrix[x][y + 1])) {
                num.append(matrix[x - 1][y]);
                num.append(matrix[x + 1][y]);
                num.append(matrix[x][y - 1]);
                num.append(matrix[x][y + 1]);
                x--;
                y--;
            } else if (Character.isDigit(matrix[x - 1][y]) && Character.isDigit(matrix[x + 1][y])) {
                num.append(matrix[x - 1][y]);
                num.append(matrix[x + 1][y]);
                x--;
            } else if (Character.isDigit(matrix[x][y - 1]) && Character.isDigit(matrix[x][y + 1])) {
                num.append(matrix[x][y - 1]);
                num.append(matrix[x][y + 1]);
                y--;
            } else if (Character.isDigit(matrix[x - 1][y])) {
                num.append(matrix[x - 1][y]);
                x--;
            } else if (Character.isDigit(matrix[x][y - 1])) {
                num.append(matrix[x][y - 1]);
                y--;
            } else if (Character.isDigit(matrix[x + 1][y])) {
                num.append(matrix[x + 1][y]);
                x++;
            } else if (Character.isDigit(matrix[x][y + 1])) {
                num.append(matrix[x][y + 1]);
                y++;
            } else break;
        }
        numbers[index] = num.toString();
        dfs(matrix, i - 1, j, visited, numbers, index);
        dfs(matrix, i + 1, j, visited, numbers, index);
        dfs(matrix, i, j - 1, visited, numbers, index);
        dfs(matrix, i, j + 1, visited, numbers, index);
    }
}
