def find_secret_number(matrix):
    rows, cols = len(matrix), len(matrix[0])
    max_num = 0

    def dfs(i, j, num):
        nonlocal max_num
        if i < 0 or i >= rows or j < 0 or j >= cols:
            return
        if matrix[i][j].isdigit():
            num = num * 10 + int(matrix[i][j])
            max_num = max(max_num, num)
            dfs(i - 1, j, num)
            dfs(i + 1, j, num)
            dfs(i, j - 1, num)
            dfs(i, j + 1, num)
        else:
            return

    for i in range(rows):
        for j in range(cols):
            if matrix[i][j].isdigit():
                dfs(i, j, int(matrix[i][j]))

    return str(max_num)


def main():
    while True:
        w, h = map(int, input().split())
        if w == 0 and h == 0:
            break
        matrix = [input() for _ in range(h)]
        print(find_secret_number(matrix))


if __name__ == "__main__":
    main()
