#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAXN 110
#define EPS 1e-6

typedef struct {
    double x, y, z, r;
} Point;

typedef struct {
    int u, v;
    double len;
} Edge;

int n;
Point p[MAXN];
Edge e[MAXN * MAXN];
int m;

double dist(int i, int j) {
    double dx = p[i].x - p[j].x;
    double dy = p[i].y - p[j].y;
    double dz = p[i].z - p[j].z;
    return sqrt(dx * dx + dy * dy + dz * dz);
}

int cmp(const void *a, const void *b) {
    Edge *x = (Edge *)a;
    Edge *y = (Edge *)b;
    return (x->len > y->len) - (x->len < y->len);
}

int find(int i) {
    if (p[i].r < EPS) return i;
    int j = find(i - 1);
    p[i].x = (p[i].x + p[j].x) / 2;
    p[i].y = (p[i].y + p[j].y) / 2;
    p[i].z = (p[i].z + p[j].z) / 2;
    p[i].r = (p[i].r + p[j].r) / 2;
    return i;
}

int main() {
    int T;
    scanf("%d", &T);
    while (T--) {
        scanf("%d", &n);
        for (int i = 1; i <= n; i++) {
            scanf("%lf %lf %lf %lf", &p[i].x, &p[i].y, &p[i].z, &p[i].r);
        }
        for (int i = 1; i <= n; i++) {
            p[i].r = 2 * p[i].r;
        }
        for (int i = 1; i <= n; i++) {
            for (int j = i + 1; j <= n; j++) {
                double d = dist(i, j);
                if (d < EPS) {
                    e[m].u = i;
                    e[m].v = j;
                    e[m].len = 0;
                    m++;
                } else {
                    e[m].u = i;
                    e[m].v = j;
                    e[m].len = d;
                    m++;
                }
            }
        }
        for (int i = 1; i <= n; i++) {
            for (int j = i + 1; j <= n; j++) {
                for (int k = j + 1; k <= n; k++) {
                    double d1 = dist(i, j);
                    double d2 = dist(j, k);
                    double d3 = dist(i, k);
                    if (d1 < EPS && d2 < EPS && d3 < EPS) {
                        e[m].u = i;
                        e[m].v = k;
                        e[m].len = d1 + d2;
                        m++;
                    }
                }
            }
        }
        qsort(e, m, sizeof(Edge), cmp);
        double ans = 0;
        for (int i = 0; i < m; i++) {
            int u = find(e[i].u);
            int v = find(e[i].v);
            if (u != v) {
                ans += e[i].len;
                p[u].r = 0;
                p[v].r = 0;
            }
        }
        printf("%.3lf\n", ans);
        for (int i = 1; i <= n; i++) {
            p[i].r = p[i].r / 2;
        }
    }
    return 0;
}
