#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

using namespace std;

struct Point {
    double x, y, z;
};

struct Cell {
    Point center;
    double radius;
    vector<int> connected;
};

double distance(const Point& p1, const Point& p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2) + pow(p1.z - p2.z, 2));
}

double distanceBetweenCells(const Cell& cell1, const Cell& cell2) {
    return distance(cell1.center, cell2.center) - cell1.radius - cell2.radius;
}

double shortestPath(vector<Cell>& cells) {
    int n = cells.size();
    vector<vector<double>> dp(n, vector<double>(n, 1e9));
    for (int i = 0; i < n; ++i) {
        dp[i][i] = 0;
    }

    for (int k = 0; k < n; ++k) {
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < n; ++j) {
                if (i != k && j != k && distanceBetweenCells(cells[i], cells[k]) + distanceBetweenCells(cells[k], cells[j]) < dp[i][j]) {
                    dp[i][j] = distanceBetweenCells(cells[i], cells[k]) + distanceBetweenCells(cells[k], cells[j]);
                }
            }
        }
    }

    double totalLength = 0;
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            if (i != j) {
                totalLength += dp[i][j];
            }
        }
    }

    return totalLength;
}

int main() {
    int n;
    while (cin >> n && n != 0) {
        vector<Cell> cells(n);
        for (int i = 0; i < n; ++i) {
            cin >> cells[i].center.x >> cells[i].center.y >> cells[i].center.z >> cells[i].radius;
        }

        cout << fixed << setprecision(3) << shortestPath(cells) << endl;
    }

    return 0;
}
