import sys
import math

def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2 + (p1[2] - p2[2])**2)

def intersect(p1, r1, p2, r2):
    return (p1[0] - p2[0])**2 + (p1[1] - p2[1])**2 + (p1[2] - p2[2])**2 <= (r1 + r2)**2

def closest_point(p1, p2, points):
    min_dist = float('inf')
    closest = None
    for point in points:
        dist = distance(p1, point)
        if dist < min_dist:
            min_dist = dist
            closest = point
    return closest

def shortest_path(points):
    n = len(points)
    dist = [[float('inf') for _ in range(n)] for _ in range(n)]
    prev = [[None for _ in range(n)] for _ in range(n)]
    for i in range(n):
        for j in range(n):
            if i == j:
                dist[i][j] = 0
            elif intersect(points[i], 1, points[j], 1):
                dist[i][j] = 0
            else:
                closest = closest_point(points[i], points[j], points)
                dist[i][j] = distance(points[i], closest)
                prev[i][j] = closest
    for k in range(n):
        for i in range(n):
            for j in range(n):
                if dist[i][k] + dist[k][j] < dist[i][j]:
                    dist[i][j] = dist[i][k] + dist[k][j]
                    prev[i][j] = prev[i][k]
    total = 0
    for i in range(n):
        for j in range(n):
            if i != j and dist[i][j] != float('inf'):
                total += dist[i][j]
    return total

def main():
    for line in sys.stdin:
        line = line.strip()
        if line == '0':
            break
        n = int(line)
        points = []
        for _ in range(n):
            x, y, z, r = map(float, input().split())
            points.append((x, y, z, r))
        print(f'{shortest_path(points):.3f}')

if __name__ == '__main__':
    main()
