#include <iostream>
#include <vector>
#include <algorithm>

int minCarpets(const std::vector<std::vector<int>>& panels) {
    int rows = panels.size();
    int cols = panels[0].size();
    std::vector<std::vector<bool>> covered(rows, std::vector<bool>(cols, false));
    int count = 0;

    while (true) {
        bool found = false;
        for (int i = 0; i < rows; ++i) {
            for (int j = 0; j < cols; ++j) {
                if (panels[i][j] == 1 && !covered[i][j]) {
                    found = true;
                    cover(covered, i, j);
                    count++;
                }
            }
        }
        if (!found) break;
    }

    return count;
}

void cover(std::vector<std::vector<bool>>& covered, int row, int col) {
    int rows = covered.size();
    int cols = covered[0].size();
    std::vector<std::vector<bool>> newCovered(rows, std::vector<bool>(cols, false));
    for (int i = 0; i < rows; ++i) {
        for (int j = 0; j < cols; ++j) {
            if (covered[i][j]) {
                newCovered[i][j] = true;
                if (i > 0 && !covered[i - 1][j]) newCovered[i - 1][j] = true;
                if (i < rows - 1 && !covered[i + 1][j]) newCovered[i + 1][j] = true;
                if (j > 0 && !covered[i][j - 1]) newCovered[i][j - 1] = true;
                if (j < cols - 1 && !covered[i][j + 1]) newCovered[i][j + 1] = true;
            }
        }
    }
    covered = newCovered;
}

int main() {
    int w, h;
    while (std::cin >> w >> h) {
        if (w == 0 && h == 0) break;
        std::vector<std::vector<int>> panels(h, std::vector<int>(w));
        for (int i = 0; i < h; ++i) {
            for (int j = 0; j < w; ++j) {
                std::cin >> panels[i][j];
            }
        }
        std::cout << minCarpets(panels) << std::endl;
    }
    return 0;
}
