#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

int main() {
    std::string line;
    while (std::getline(std::cin, line)) {
        if (line.empty()) continue;

        std::vector<std::string> words;
        size_t pos = 0;
        while ((pos = line.find(' ')) != std::string::npos) {
            words.push_back(line.substr(0, pos));
            line.erase(0, pos + 1);
        }
        words.push_back(line);

        int shift = 0;
        for (int i = 0; i < 26; i++) {
            for (const auto& word : words) {
                std::string decodedWord;
                for (char c : word) {
                    if (c >= 'a' && c <= 'z') {
                        decodedWord += 'a' + (c - 'a' - i + 26) % 26;
                    } else {
                        decodedWord += c;
                    }
                }
                if (decodedWord.find("the") != std::string::npos ||
                    decodedWord.find("this") != std::string::npos ||
                    decodedWord.find("that") != std::string::npos) {
                    shift = i;
                    break;
                }
            }
            if (shift != 0) break;
        }

        std::string decodedLine;
        for (const auto& word : words) {
            std::string decodedWord;
            for (char c : word) {
                if (c >= 'a' && c <= 'z') {
                    decodedWord += 'a' + (c - 'a' - shift + 26) % 26;
                } else {
                    decodedWord += c;
                }
            }
            decodedLine += decodedWord + " ";
        }
        std::cout << decodedLine << std::endl;
    }
    return 0;
}
