import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int w = scanner.nextInt();
            int h = scanner.nextInt();
            int[][] grid = new int[h][w];
            for (int i = 0; i < h; i++) {
                for (int j = 0; j < w; j++) {
                    grid[i][j] = scanner.nextInt();
                }
            }
            if (w == 0 && h == 0) {
                break;
            }
            int count = 0;
            for (int i = 0; i < h; i++) {
                for (int j = 0; j < w; j++) {
                    if (grid[i][j] == 1) {
                        int x = 0;
                        while (i + x < h && j + x < w && grid[i + x][j + x] == 1) {
                            x++;
                        }
                        count++;
                        for (int k = 0; k < x; k++) {
                            for (int l = 0; l < x; l++) {
                                grid[i + k][j + l] = 0;
                            }
                        }
                    }
                }
            }
            System.out.println(count);
        }
    }
}
