import sys

def min_carpets(w, h, panels):
    def dfs(x, y, size):
        if x + size > w or y + size > h:
            return 0
        count = 0
        for i in range(x, x + size):
            for j in range(y, y + size):
                if panels[i][j] == 1:
                    count += 1
        for i in range(x, x + size):
            for j in range(y, y + size):
                if panels[i][j] == 1:
                    panels[i][j] = 0
        return 1 + dfs(x, y, size * 2) + dfs(x + size, y, size * 2) + dfs(x, y + size, size * 2) + dfs(x + size, y + size, size * 2)

    min_size = 1
    while min_size * 2 <= min(w, h):
        min_size *= 2
    return dfs(0, 0, min_size)

def main():
    while True:
        w, h = map(int, sys.stdin.readline().split())
        if w == 0 and h == 0:
            break
        panels = []
        for _ in range(h):
            panels.append(list(map(int, sys.stdin.readline().split())))
        print(min_carpets(w, h, panels))

if __name__ == "__main__":
    main()
