import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            int r = scanner.nextInt();
            if (n == 0 && r == 0) {
                break;
            }
            int[] cards = new int[n];
            for (int i = 0; i < n; i++) {
                cards[i] = i + 1;
            }
            for (int i = 0; i < r; i++) {
                int p = scanner.nextInt() - 1;
                int c = scanner.nextInt();
                int[] temp = new int[c];
                System.arraycopy(cards, p, temp, 0, c);
                System.arraycopy(cards, 0, cards, c, p);
                System.arraycopy(temp, 0, cards, 0, c);
            }
            System.out.println(cards[0]);
        }
    }
}
