#include <stdio.h>
#include <stdlib.h>

#define MAX_W 20
#define MAX_H 20

int visited[MAX_W][MAX_H];
int w, h;

void dfs(int x, int y) {
    if (x < 0 || x >= w || y < 0 || y >= h || visited[x][y] || grid[y][x] == '#') {
        return;
    }
    visited[x][y] = 1;
    dfs(x - 1, y);
    dfs(x + 1, y);
    dfs(x, y - 1);
    dfs(x, y + 1);
}

int main() {
    int i, j, x, y;
    char c;
    int count;

    while (scanf("%d %d", &w, &h) == 2) {
        if (w == 0 && h == 0) {
            break;
        }
        for (i = 0; i < w; i++) {
            for (j = 0; j < h; j++) {
                scanf(" %c", &c);
                grid[j][i] = c;
                visited[i][j] = 0;
            }
        }
        scanf(" %c", &c);
        for (i = 0; i < h; i++) {
            for (j = 0; j < w; j++) {
                if (grid[i][j] == '@') {
                    x = j;
                    y = i;
                }
            }
        }
        dfs(x, y);
        count = 0;
        for (i = 0; i < w; i++) {
            for (j = 0; j < h; j++) {
                if (visited[i][j]) {
                    count++;
                }
            }
        }
        printf("%d\n", count);
        for (i = 0; i < h; i++) {
            for (j = 0; j < w; j++) {
                scanf(" %c", &c);
            }
        }
    }
    return 0;
}
