#include <iostream>
#include <vector>
#include <queue>
#include <string>

using namespace std;

int countReachableTiles(const vector<string>& grid, int w, int h) {
    int count = 0;
    vector<vector<bool>> visited(w, vector<bool>(h, false));
    queue<pair<int, int>> q;

    for (int i = 0; i < w; ++i) {
        for (int j = 0; j < h; ++j) {
            if (grid[i][j] == '@') {
                q.push({i, j});
                visited[i][j] = true;
                count++;
                break;
            }
        }
    }

    int dx[] = {-1, 0, 1, 0};
    int dy[] = {0, -1, 0, 1};

    while (!q.empty()) {
        int x = q.front().first;
        int y = q.front().second;
        q.pop();

        for (int i = 0; i < 4; ++i) {
            int nx = x + dx[i];
            int ny = y + dy[i];

            if (nx >= 0 && nx < w && ny >= 0 && ny < h && grid[nx][ny] == '.' && !visited[nx][ny]) {
                q.push({nx, ny});
                visited[nx][ny] = true;
                count++;
            }
        }
    }

    return count;
}

int main() {
    string line;
    while (getline(cin, line)) {
        if (line == "0 0") break;

        istringstream iss(line);
        int w, h;
        iss >> w >> h;

        vector<string> grid(w);
        for (int i = 0; i < w; ++i) {
            getline(cin, grid[i]);
        }

        cout << countReachableTiles(grid, w, h) << endl;
    }

    return 0;
}
