import java.util.*;

public class Main {
    static int[][] directions = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};
    static int[][] visited;

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            String[] input = scanner.nextLine().split(" ");
            if (input[0].equals("0") && input[1].equals("0")) break;
            int w = Integer.parseInt(input[0]);
            int h = Integer.parseInt(input[1]);
            char[][] grid = new char[h][w];
            for (int i = 0; i < h; i++) {
                grid[i] = scanner.nextLine().toCharArray();
            }
            visited = new int[h][w];
            int count = dfs(grid, 0, 0, w, h);
            System.out.println(count);
        }
    }

    public static int dfs(char[][] grid, int x, int y, int w, int h) {
        if (x < 0 || x >= h || y < 0 || y >= w || grid[x][y] == '#' || visited[x][y] == 1) return 0;
        visited[x][y] = 1;
        int count = 1;
        for (int[] dir : directions) {
            count += dfs(grid, x + dir[0], y + dir[1], w, h);
        }
        return count;
    }
}
